import React from "react";
import PersonComponent from "./Person/PersonComponent";
import PersonsFun from "./Person/PersonFun";

const PersonsShowComponent = (props) =>
  props.info.map((person, index) => {
    return (
      <PersonComponent
        info={person}
        click={() => props.clicked(index)}
        key={person.id}
      />
    );
  });


const PersonsShowFunction = (pros) =>
  this.state.personsName.map((person) => {
    return (
      <PersonFun
        info={person}
        key={person.id}
        change={(event) => this.nameChangedHandler(event, person.id)}
      />
    );
  });

export default PersonsShowComponent;
