import "./App.css";
import { Component } from "react";
import { render } from "@testing-library/react";
import PersonComponent from "./Person/PersonType";
import PersonFun from "./Person/PersonFun";

class App extends Component {
  state = {
    personsName: [
      { name: "Foivos", age: 28 },
      { name: "Evi", age: 27 },
    ],
  };

  switchNameHandler = (newName, newAge) => {
    console.log("CLICKED!!");
    this.setState({
      personsName: [
        {
          name: newName,
          age: newAge,
        },
        {
          name: "Evaggelia",
          age: 37,
        },
      ],
    });
    console.log(this.state.personsName);
  };

  render() {
    // console.log(this.state.personsName);
    return (
      <div className="App">
        <button onClick={this.switchNameHandler.bind(this, "Fanourios", "40")}>
          Change Name
        </button>
        <h1>To App pou arxizw kai katalavainw</h1>
        <PersonComponent info={this.state.personsName[1]} />
        <PersonFun
          click={this.switchNameHandler.bind(this, "KING", 37)}
          info={this.state.personsName[0]}
        />
      </div>
    );
  }
}

export default App;

