import "./App.css";
import { Component } from "react";
import { render } from "@testing-library/react";
import PersonComponent from "./Person/PersonType";
import PersonFun from "./Person/PersonFun";

class App extends Component {
  state = {
    personsName: [
      { name: "Foivos", age: 28 },
      { name: "Evi", age: 27 },
    ],
  };

  switchNameHandler = (state) => {
    console.log("CLICKED!!");
    this.setState().personsName[0].name = "Fanourios";
    this.state.personsName[1].name = "Evaggelia";
    console.log(this.state.personsName[1].name);
    console.log(this.state.personsName[0].name);
    console.log(this.state);
  };

  render() {
    console.log(this.state.personsName);
    return (
      <div className="App">
        <button onClick={this.switchNameHandler}>Change Name</button>
        <h1>To App pou arxizw kai katalavainw</h1>
        <PersonComponent info={this.state.personsName[1]} />
        <PersonFun info={this.state.personsName[0]} />
      </div>
    );
  }
}

export default App;
