import "./App.css";
import { Component } from "react";
import { render } from "@testing-library/react";
import PersonComponent from "./Person/PersonType";
import PersonFun from "./Person/PersonFun";

class App extends Component {
  state = {
    personsName: [
      { name: "Foivos", age: 28 },
      { name: "Evi", age: 27 },
    ],
  };

  switchNameHandler = (state) => {
    console.log("CLICKED!!");
    this.state.personsName[0].name = "Fanourios";
    this.state.personsName[1].name = "Evaggelia";
  };

  render() {
    console.log(this.state.personsName);
    return (
      <div className="App">
        <button onClick={this.switchNameHandler}>Change Name</button>

      </div>
    );
  }
}

export default App;
